  },
  label: {
    style: {
      fill: '#fdae6b'
    }
  }
});
chart.axis('waiting', {
  title: {}
});
chart.tooltip({
  shared: true,
});
chart.interval()
  .position('time*waiting')
  .color('#3182bd');
chart.line()
  .position('time*people')
  .color('#fdae6b')
  .size(3)
  .shape('smooth');
chart.point()
  .position('time*people')
  .color('#fdae6b')
  .size(3)
  .shape('circle');

chart.interaction('active-region');
chart.render();

