fetch('../data/gaussion-distribution.json')
  .then(res => res.json())
  .then(data => {
    const dv = new DataSet.View().source(data)
      .transform({
        sizeByCount: true, // calculate bin size by binning count
        type: 'bin.hexagon',
        fields: ['x', 'y'], // 对应坐标轴上的一个点
        bins: [10, 5]
