      container: 'container',
      autoFit: true,
      height: 500
    });
    chart.data(dv.rows);
    chart.tooltip({
      showTitle: false,
      showMarkers: false
    });
    chart.legend({
      offset: 40
    });
    chart.axis('x', {
      grid: {
        line: {
          style: {
            stroke: '#d9d9d9',
            lineWidth: 1,
            lineDash: [2, 2]
          }
        }
      }
    });
    chart
      .polygon()
      .position('x*y')
      .color('count', '#BAE7FF-#1890FF-#0050B3')
      .style({
        lineWidth: 1,
        stroke: '#fff'
      });
    chart.interaction('element-active');
    chart.render();
  });

