/** 数据调整配置项定义，`geometry.adjust({})` */
declare interface AdjustOption {
    /** 数据调整类型。 */
    readonly type: AdjustType;
    /**
     * 该属性只对 'dodge' 类型生效，取 0 到 1 范围的值（相对于每个柱子宽度），用于控制一个分组中柱子之间的间距。
     *
     * ![image](https://gw.alipayobjects.com/mdn/rms_2274c3/afts/img/A*ps3pToOg2nwAAAAAAAAAAABkARQnAQ)
     */
    readonly marginRatio?: number;
    /**
     * 该属性只对 'dodge' 类型生效，声明以哪个数据字段为分组依据。
     */
    readonly dodgeBy?: string;
    /**
     * 该属性只对 'stack' 类型生效，用于控制是否对数据进行反序操作。
     */
    readonly reverseOrder?: boolean;
}

/** G2 提供的 adjust 类型 */
declare type AdjustType = 'stack' | 'jitter' | 'dodge' | 'symmetric';

/** Options 中 legends 的配置定义 */
declare type AllLegendsOptions = LegendCfg | Record<string, LegendOption> | boolean;

/** 用户配置的动画，属性均可选 */
declare interface AnimateCfg {
    /** 动画缓动函数 */
    readonly easing?: string | AnimateEasingCallback;
    /** 动画执行函数 */
    readonly animation?: string;
    /** 动画执行时间 */
    readonly duration?: number | AnimateDurationCallback;
    /** 动画延迟时间 */
    readonly delay?: number | AnimateDelayCallback;
    /** 动画执行结束后的回调函数 */
    readonly callback?: () => any;
}

/** delay 的回调函数， 入参 data 为对应的原始数据记录 */
declare type AnimateDelayCallback = (data: Datum) => number;

/** duration 的回调函数， 入参 data 为对应的原始数据记录 */
declare type AnimateDurationCallback = (data: Datum) => number;

/** easing 的回调函数， 入参 data 为对应的原始数据记录 */
declare type AnimateEasingCallback = (data: Datum) => string;

/** Geometry 动画参数配置。geometry.animate() */
declare interface AnimateOption {
    /** chart 初始化渲染时的入场动画，false/null 表示关闭入场动画。 */
    appear?: AnimateCfg | false | null;
    /** chart 发生更新时，新增元素的入场动画，false/null 表示关闭入场动画。 */
    enter?: AnimateCfg | false | null;
    /** 更新动画配置，false/null 表示关闭更新动画。 */
    update?: AnimateCfg | false | null;
    /** 销毁动画配置，false/null 表示关闭销毁动画。 */
    leave?: AnimateCfg | false | null;
}

/**
 * Annotation controller, 主要作用:
 * 1. 创建 Annotation: line、text、arc ...
 * 2. 生命周期: init、layout、render、clear、destroy
 */
declare class Annotation extends Controller<AnnotationBaseOption[]> {
    private foregroundContainer;
    private backgroundContainer;
    private htmlContainer;
    private cache;
    constructor(view: View);
    get name(): string;
    init(): void;
    /**
     * 因为 annotation 需要依赖坐标系信息，所以 render 阶段为空方法，实际的创建逻辑都在 layout 中
     */
    layout(): void;
    render(): void;
    /**
     * 更新
     */
    update(): void;
    /**
     * 清空
     * @param includeOption 是否清空 option 配置项
     */
    clear(includeOption?: boolean): void;
    destroy(): void;
    /**
     * 复写基类的方法
     */
    getComponents(): ComponentOption[];
    /**
     * region filter 比较特殊的渲染时机
     * @param doWhat
     */
    private onAfterRender;
    private createAnnotation;
    private initHTMLContainer;
    private clearHTMLContainer;
    annotation(option: any): void;
    /**
     * 创建 Arc
     * @param option
     * @returns AnnotationController
     */
    arc(option: ArcOption): this;
    /**
     * 创建 image
     * @param option
     * @returns AnnotationController
     */
    image(option: ImageOption): this;
    /**
     * 创建 Line
     * @param option
     * @returns AnnotationController
     */
    line(option: LineOption): this;
    /**
     * 创建 Region
     * @param option
     * @returns AnnotationController
     */
    region(option: RegionOption): this;
    /**
     * 创建 Text
     * @param option
     * @returns AnnotationController
     */
    text(option: TextOption): this;
    /**
     * 创建 DataMarker
     * @param option
     * @returns AnnotationController
     */
    dataMarker(option: DataMarkerOption): this;
    /**
     * 创建 DataRegion
     * @param option
     * @returns AnnotationController
     */
    dataRegion(option: DataRegionOption): void;
    /**
     * 创建 RegionFilter
     * @param option
     * @returns AnnotationController
     */
    regionFilter(option: RegionFilterOption): void;
    /**
     * 创建 ShapeAnnotation
     * @param option
     */
    shape(option: ShapeAnnotationOption): void;
    /**
     * 创建 HtmlAnnotation
     * @param option
     */
    html(option: HtmlAnnotationOption): void;
    /**
     * parse the point position to [x, y]
     * @param p Position
     * @returns { x, y }
     */
    private parsePosition;
    /**
     * parse all the points between start and end
     * @param start
     * @param end
     * @return Point[]
     */
