    private getAnnotationTheme;
    /**
     * 创建或者更新 annotation
     * @param option
     */
    private updateOrCreate;
    /**
     * 更新缓存，以及销毁组件
     * @param updated 更新或者创建的组件
     */
    private syncCache;
    /**
     * 获得缓存组件的 key
     * @param option
     */
    private getCacheKey;
}

/** Annotation 定义的通用属性，chart.annotation().line({}) */
declare interface AnnotationBaseOption {
    readonly type?: string;
    /** 指定 annotation 是否绘制在 canvas 最上层，默认为 false, 即绘制在最下层 */
    readonly top?: boolean;
    /** 是否进行动画 */
    readonly animate?: boolean;
    /** 动画参数配置，当且仅当 `animate` 属性为 true，即动画开启时生效。 */
    readonly animateOption?: ComponentAnimateOption;
    /** x 方向的偏移量 */
    readonly offsetX?: number;
    /** y 方向的偏移量 */
    readonly offsetY?: number;
}

/** Annotation 位置相关属性的类型定义 */
declare type AnnotationPosition = [number | string, number | string] | Record<string, number | string> | AnnotationPositionCallback;

/** Annotation position 回调函数 */
declare type AnnotationPositionCallback = (xScales: Scale[] | Record<string, Scale>, yScales: Scale[] | Record<string, Scale>) => [number | string, number | string];

/** 使用 Arc Annotation 组件的配置定义 */
declare type ArcOption = RegionPositionBaseOption;

/** 图形属性配置项定义，如 geometry.position({}) */
declare interface AttributeOption {
    /** 映射的属性字段。 */
    fields?: string[];
    /** 回调函数。 */
    callback?: (...args: any[]) => any;
    /** 指定常量映射规则。 */
    values?: any[];
}

/** 坐标轴配置属性，chart.axis() */
declare interface AxisCfg {
    /**
     * 适用于直角坐标系，设置坐标轴的位置。
     */
    position?: 'top' | 'bottom' | 'right' | 'left';
    /**
     * 坐标轴线的配置项，null 表示不展示。
     * 属性结构如下：
     *
     * ```ts
     * {
     *   style?: ShapeAttrs; // 坐标轴线的样式配置项
     * }
     * ```
     *
     * 详见 {@link https://github.com/antvis/component/blob/81890719a431b3f9088e0c31c4d5d382ef0089df/src/types.ts#L91|AxisLineCfg}
     */
    line?: AxisLineCfg | null;
    /**
     * 坐标轴刻度线线的配置项，null 表示不展示。
     * 属性结构如下：
     *
     * ```ts
     * {
     *   style?: ShapeAttrs; // 坐标轴刻度线的样式配置项
     *   alignTick?: boolean; // 是否同 tick 对齐
     *   length?: number;  // 长度
     * }
     * ```
     *
     * 详见 {@link https://github.com/antvis/component/blob/81890719a431b3f9088e0c31c4d5d382ef0089df/src/types.ts#L103|AxisTickLineCfg}
     */
    tickLine?: AxisTickLineCfg | null;
    /**
     * 坐标轴子刻度线的配置项，null 表示不展示。
     * 属性结构如下：
     *
     * ```ts
     * {
     *   style?: ShapeAttrs; // 坐标轴刻度线的样式配置项
     *   count?: number; // 子刻度个数
     *   length?: number; // 子刻度线长度
     * }
     * ```
     *
     * 详见 {@link https://github.com/antvis/component/blob/81890719a431b3f9088e0c31c4d5d382ef0089df/src/types.ts#L169|AxisSubTickLineCfg}
     */
    subTickLine?: AxisSubTickLineCfg | null;
    /**
     * 标题的配置项，null 表示不展示。
     * 属性结构如下：
     *
     * ```ts
     * {
     *   offset?: number; // 标题距离坐标轴的距离
     *   style?: ShapeAttrs; // 标题文本配置项
     *   autoRotate?: boolean; // 是否自动旋转
     * }
     * ```
     *
     * 详见 {@link https://github.com/antvis/component/blob/81890719a431b3f9088e0c31c4d5d382ef0089df/src/types.ts#L191|AxisTitleCfg}
     */
    title?: AxisTitleCfg | null;
    /**
     * 文本标签的配置项，null 表示不展示。
     * 属性结构如下：
     *
     * ```ts
     * {
     *   // 坐标轴文本的样式
     *   style?: ShapeAttrs;
     *   // label 的偏移量
     *   offset?: number;
     *   // 文本旋转角度
     *   rotate?: number;
     *   // 格式化函数
     *   formatter?: (text: string, item: ListItem, index: number) => any;
     *   // 是否自动旋转，默认 true
     *   autoRotate?: boolean | (isVertical: boolean, labelGroup: IGroup, limitLength?: number) => boolean; | string;
     *   // 是否自动隐藏，默认 false
     *   autoHide?: boolean | (isVertical: boolean, labelGroup: IGroup, limitLength?: number) => boolean; | string;
     *   // 是否自动省略，默认 false
     *   autoEllipsis?: boolean | (isVertical: boolean, labelGroup: IGroup, limitLength?: number) => boolean; | string;
     * }
     * ```
     *
     * 详见 {@link https://github.com/antvis/component/blob/81890719a431b3f9088e0c31c4d5d382ef0089df/src/types.ts#L127|AxisLabelCfg}
     */
    label?: AxisLabelCfg | null;
    /** 坐标轴网格线的配置项，null 表示不展示。 */
    grid?: AxisGridCfg | null;
    /** 动画开关，默认开启。 */
    animate?: boolean;
    /** 动画参数配置。 */
    animateOption?: ComponentAnimateOption;
    /** 标记坐标轴 label 的方向，左侧为 1，右侧为 -1。 */
    verticalFactor?: number;
    /**
     * 配置坐标轴垂直方向的最大限制长度，对文本自适应有很大影响。
     * 1. 可以直接设置像素值，如 100；
     * 2. 也可设置绝对值，如 0.2，如果是 x 轴，则相对于图表的高度，如果是 y 轴，则相对于图表的宽度
     *
     * 在 G2 中，x 轴的文本默认最大高度为图表高度的 1/2，y 轴的文本默认最大长度为图表宽度的 1/3
     */
    verticalLimitLength?: number;
}

/** 坐标轴网格线的配置属性 */
declare interface AxisGridCfg {
    /**
     * 线的样式。
     * 属性结构如下：
     *
     * ```ts
     * {
     *   type?: string; // 栅格线的类型，'line' 或者 'circle'
     *   style?: ShapeAttrs; // 栅格线的样式配置项
     * }
     * ```
     *
     * 详见 {@link https://github.com/antvis/component/blob/81890719a431b3f9088e0c31c4d5d382ef0089df/src/types.ts#L407|GridLineCfg}
     */
    line?: GridLineCfg;
    /**
     * 两个栅格线间的填充色。
     */
    alternateColor?: string | string[];
    /**
     * 对于 circle 是否关闭 grid。
     */
    closed?: boolean;
    /**
     * 是否同刻度线对齐，如果值为 false，则会显示在两个刻度中间。
     * ![image](https://gw.alipayobjects.com/mdn/rms_2274c3/afts/img/A*YX6fS4GTTvMAAAAAAAAAAABkARQnAQ)
     */
    alignTick?: boolean;
}

/** chart.axis() 参数类型 */
declare type AxisOption = AxisCfg | boolean;

/**
 * G2 Chart、View、Geometry 以及 Element 等的基类，提供事件以及一些通用的方法。
 */
declare class Base extends EE {
    /** 是否可见 */
    visible: boolean;
    /** 标识对象是否已销毁 */
    destroyed: boolean;
    constructor(cfg: BaseCfg);
    /**
     * 显示。
     */
    show(): void;
    /**
     * 隐藏。
     */
    hide(): void;
    /**
     * 销毁。
     */
    destroy(): void;
    /**
     * 显示或者隐藏。
     * @param visible
     * @returns
     */
    changeVisible(visible: boolean): void;
}

declare interface BaseCfg {
    visible?: boolean;
}

/**
 * 用于包围盒计算。
 */
declare class BBox_2 {
    /** x 轴坐标系 */
    x: number;
    /** y 轴坐标系 */
    y: number;
    /** 包围盒高度 */
    height: number;
    /** 包围盒宽度 */
    width: number;
    static fromRange(minX: number, minY: number, maxX: number, maxY: number): BBox_2;
    static fromObject(bbox: BBox): BBox_2;
    constructor(x?: number, y?: number, width?: number, height?: number);
    get minX(): number;
    get maxX(): number;
    get minY(): number;
    get maxY(): number;
    get tl(): Point;
    get tr(): Point;
    get bl(): Point;
    get br(): Point;
    get top(): Point;
    get right(): Point;
    get bottom(): Point;
    get left(): Point;
    /**
     * 包围盒是否相等
     * @param {BBox} bbox 包围盒
     * @returns      包围盒是否相等
     */
    isEqual(bbox: BBox_2): boolean;
    /**
     * 是否包含了另一个包围盒
     * @param child
     */
    contains(child: BBox_2): boolean;
    /**
     * 克隆包围盒
     * @returns 包围盒
     */
    clone(): BBox_2;
    /**
     * 取并集
     * @param subBBox
     */
    add(...subBBox: BBox_2[]): BBox_2;
    /**
     * 取交集
     * @param subBBox
     */
    merge(...subBBox: BBox_2[]): BBox_2;
    /**
     * bbox 剪裁
     * @param subBBox
     * @param direction
     */
    cut(subBBox: BBox_2, direction: DIRECTION): BBox_2;
    /**
     * 收缩形成新的
     * @param gap
     */
    shrink(gap: Padding): BBox_2;
    /**
     * 扩张形成新的
     * @param gap
     */
    expand(gap: Padding): BBox_2;
    /**
     * get the gap of two bbox, if not exceed, then 0
     * @param bbox
     * @returns [top, right, bottom, left]
     */
    exceed(bbox: BBox_2): Padding;
    /**
     * 是否碰撞
     * @param bbox
     */
    collide(bbox: BBox_2): boolean;
    /**
     * 获取包围盒大小
     * @returns 包围盒大小
     */
    size(): number;
    /**
     * 点是否在 bbox 中
     * @param p
     */
    isPointIn(p: Point): boolean;
}

/**
 * Chart 类，是使用 G2 进行绘图的入口。
 */
export declare class Chart extends View {
    /** Chart 的 DOM 容器 */
    ele: HTMLElement;
    /** 图表宽度 */
    width: number;
    /** 图表高度 */
    height: number;
    /** 是否开启局部刷新 */
    localRefresh: boolean;
    /** 是否自适应 DOM 容器宽高，默认为 false，需要用户手动指定宽高 */
    autoFit: boolean;
    /** 图表渲染引擎 */
    renderer: 'canvas' | 'svg';
    private wrapperElement;
    constructor(props: ChartCfg);
    private initDefaultInteractions;
    /**
     * 改变图表大小，同时重新渲染。
     * @param width 图表宽度
     * @param height 图表高度
     * @returns
     */
    changeSize(width: number, height: number): this;
    /**
     * 销毁图表，同时解绑事件，销毁创建的 G.Canvas 实例。
     * @returns void
     */
    destroy(): void;
    /**
     * 显示或隐藏图表
     * @param visible 是否可见，true 表示显示，false 表示隐藏
     * @returns
     */
    changeVisible(visible: boolean): this;
    /**
     * 自动根据容器大小 resize 画布
     */
    forceFit(): void;
    private updateCanvasStyle;
    private bindAutoFit;
    private unbindAutoFit;
    /**
     * when container size changed, change chart size props, and re-render.
     */
    private onResize;
}

/** Chart 构造方法的入参 */
declare interface ChartCfg extends Omit<ViewCfg, 'parent' | 'canvas' | 'foregroundGroup' | 'middleGroup' | 'backgroundGroup' | 'region'> {
    /** 指定 chart 绘制的 DOM，可以传入 DOM id，也可以直接传入 dom 实例。 */
    readonly container: string | HTMLElement;
    /** 图表宽度。 */
    readonly width?: number;
    /** 图表高度。 */
    readonly height?: number;
    /**
     * 图表是否自适应容器宽高，默认为 false，用户需要手动设置 width 和 height。
     * 当 `autoFit: true` 时，会自动取图表容器的宽高，如果用户设置了 height，那么会以用户设置的 height 为准。
     */
    readonly autoFit?: boolean;
    /** 指定渲染引擎，默认使用 canvas。 */
    readonly renderer?: Renderer;
    /** 设置设备像素比，默认取浏览器的值 `window.devicePixelRatio`。 */
    readonly pixelRatio?: number;
    /**
     * 是否开启局部刷新，默认开启。
     */
    readonly localRefresh?: boolean;
    /** 支持 CSS transform，开启后图表的交互以及事件将在页面设置了 css transform 属性时生效，默认关闭。 */
    readonly supportCSSTransform?: boolean;
    /**
     * 配置图表默认交互，仅支持字符串形式。
     */
    readonly defaultInteractions?: string[];
}

/** circle 分面类型配置 */
declare interface CircleCfg extends FacetCfg<CircleData> {
    /** 分面标题配置。 */
    readonly title?: FacetTitle;
}

declare interface CircleData extends FacetData {
}

/** geometry.color() 图形属性回调函数定义 */
declare type ColorAttrCallback = (...args: any[]) => string;

/**
 * 组件的类型，可能会影响到布局算法
 */
declare enum COMPONENT_TYPE {
    /** axis 组件 */
    AXIS = "axis",
    /** grid 组件 */
    GRID = "grid",
    /** legend 组件 */
    LEGEND = "legend",
    /** tooltip 组件 */
    TOOLTIP = "tooltip",
    /** annotation 组件 */
    ANNOTATION = "annotation",
    /** 缩略轴组件 */
    SLIDER = "slider",
    /** 滚动条组件 */
    SCROLLBAR = "scrollbar",
    /** 其他组件，自定义组件 */
    OTHER = "other"
}

/** 目前组件动画允许的参数配置 */
declare interface ComponentAnimateCfg {
    /** 动画执行时间 */
    readonly duration?: number;
    /** 动画缓动函数 */
    readonly easing?: string;
    /** 动画延迟时间 */
    readonly delay?: number;
}

/** 组件各个动画类型配置 */
declare interface ComponentAnimateOption {
    /** 初入场动画配置 */
    appear?: ComponentAnimateCfg;
    /** 更新动画配置 */
    update?: ComponentAnimateCfg;
    /** 更新后新入场的动画配置 */
    enter?: ComponentAnimateCfg;
    /** 离场动画配置 */
    leave?: ComponentAnimateCfg;
}

/**
 * @ignore
 * 组件及布局的信息
 */
declare interface ComponentOption {
    readonly id?: string;
    readonly component: GroupComponent | HtmlComponent;
    readonly layer: LAYER;
    direction: DIRECTION;
    readonly type: COMPONENT_TYPE;
    readonly extra?: any;
}

declare interface Condition {
    readonly field: string;
    readonly value: any;
    readonly values: any[];
}

/**
 * Component Controller 规范需要定义的基类
 * 1. 规范的 option 输入
 * 2. 统一的信息获取 API
 * 3. 明确定义的组件事件（名称、数据）
 */
declare abstract class Controller<O = unknown> {
    /** 是否可见 */
    visible: boolean;
    protected view: View;
    /** option 配置，不同组件有自己不同的配置结构 */
    protected option: O;
    /** 所有的 component */
    protected components: ComponentOption[];
    constructor(view: View);
    abstract get name(): string;
    /**
     * init the component
     */
    abstract init(): any;
    /**
     * render the components
     */
    abstract render(): any;
    /**
     * update the components
     */
    /**
     * do layout
     */
    abstract layout(): any;
    /**
     * 组件的更新逻辑
     *  - 根据字段为标识，为每一个组件生成一个 id，放到 option 中
     *  - 更新的时候按照 id 去做 diff，然后对同的做处理
     *  - 创建增加的
     *  - 更新已有的
     *  - 销毁删除的
     */
    abstract update(): any;
    /**
     * clear
     * @param includeOption 是否清空 option 配置项（used in annotation）
     */
    clear(includeOption?: boolean): void;
    /**
     * destroy the component
     */
    destroy(): void;
    /**
     * get all components
     * @returns components array
     */
    getComponents(): ComponentOption[];
    /**
     * change visibility of component
     * @param visible
     */
    changeVisible(visible: boolean): void;
}

/** 坐标系支持的 action 配置 */
declare type CoordinateActions = CoordinateRotate | CoordinateReflect | CoordinateScale | CoordinateTranspose;

/** 极坐标系支持的配置属性 */
declare interface CoordinateCfg {
    /**
     * 用于极坐标，配置起始弧度。
     */
    startAngle?: number;
    /**
     * 用于极坐标，配置结束弧度。
     */
    endAngle?: number;
    /**
     * 用于极坐标，配置极坐标半径，0 - 1 范围的数值。
     */
    radius?: number;
    /**
     * 用于极坐标，极坐标内半径，0 -1 范围的数值。
     */
    innerRadius?: number;
}

/**
 * coordinate controller，职责：
 * 1. 创建实例
 * 2. 暂存配置
 */
declare class CoordinateController {
    private option;
    private coordinate;
    constructor(option?: CoordinateOption);
    /**
     * 更新配置
     * @param option
     */
    update(option: CoordinateOption): this;
    /**
     * 是否存在某一个 action
     * @param actionName
     */
    hasAction(actionName: string): boolean;
    /**
     * 创建坐标系对象
     * @param start 起始位置
     * @param end   结束位置
     * @return 坐标系实例
     */
    create(start: Point_2, end: Point_2): Coordinate;
    /**
     * 更新坐标系对象
     * @param start 起始位置
     * @param end   结束位置
     * @return 坐标系实例
     */
    adjust(start: Point_2, end: Point_2): Coordinate;
    /**
     * 旋转弧度
     * @param angle
     */
    rotate(angle: number): this;
    /**
     * 镜像
     * @param dim
     */
    reflect(dim: 'x' | 'y'): this;
    /**
     * scale
     * @param sx
     * @param sy
     */
    scale(sx: number, sy: number): this;
    /**
     * 对角变换
     */
    transpose(): this;
    /**
     * 获取配置
     */
    getOption(): CoordinateOption;
    /**
     * 获得 coordinate 实例
     */
    getCoordinate(): Coordinate;
    /**
     * 包装配置的默认值
     * @param option
     */
    private wrapperOption;
    /**
     * coordinate 实例执行 actions
     * @params includeActions 如果没有指定，则执行全部，否则，执行指定的 action
     */
    private execActions;
}

/** 坐标系配置 */
declare interface CoordinateOption {
    /** 坐标系类型 */
    type?: 'polar' | 'theta' | 'rect' | 'cartesian' | 'helix';
    /** 坐标系配置项，目前常用于极坐标。 */
    cfg?: CoordinateCfg;
    /**
     * 坐标系变换操作:
     * 1. rotate 表示旋转，使用弧度制。
     * 2. scale 表示沿着 x 和 y 方向的缩放比率。
     * 3. reflect 表示沿 x 方向镜像或者沿 y 轴方向映射。
     * 4. transpose 表示 x，y 轴置换。
     */
    actions?: CoordinateActions[];
}

declare type CoordinateReflect = ['reflect', 'x' | 'y'];

declare type CoordinateRotate = ['rotate', number];

declare type CoordinateScale = ['scale', number, number];

declare type CoordinateTranspose = ['transpose'];

/** geometry.custom() custom 自定义的配置，可以传入任何数据 */
declare type CustomOption = any;

declare type Data = Datum[];

/** 使用 DataMarker Annotation 组件的配置定义 */
declare interface DataMarkerOption extends PointPositionBaseOption {
    /** point 设置 */
    readonly point?: null | {
        style?: ShapeAttrs;
    };
    /** line 设置 */
    readonly line?: null | {
        style?: ShapeAttrs;
        length?: number;
    };
    /** text 设置 */
    readonly text: null | EnhancedTextCfg;
    /** 文本超出绘制区域时，是否自动调节文本方向，默认为 true */
    readonly autoAdjust?: boolean;
    /** 朝向，默认为 upward，可选值为 'upward' 或者 'downward' */
    readonly direction?: 'upward' | 'downward';
}

/** 使用 DataRegion Annotation 组件的配置定义 */
declare interface DataRegionOption extends RegionPositionBaseOption {
    /** line长度，default为 0 */
    readonly lineLength?: number;
    /** 标注区间的配置 */
    readonly region?: null | {
        style?: ShapeAttrs;
    };
    /** 文本的配置 */
    readonly text?: null | EnhancedTextCfg;
}

/** 数据的定义 */
declare type Datum = Record<string, any>;

/**
 * 组件在画布的布局方位 12 方位
 */
declare enum DIRECTION {
    TOP = "top",
    TOP_LEFT = "top-left",
    TOP_RIGHT = "top-right",
    RIGHT = "right",
    RIGHT_TOP = "right-top",
    RIGHT_BOTTOM = "right-bottom",
    LEFT = "left",
    LEFT_TOP = "left-top",
    LEFT_BOTTOM = "left-bottom",
    BOTTOM = "bottom",
    BOTTOM_LEFT = "bottom-left",
    BOTTOM_RIGHT = "bottom-right",
    RADIUS = "radius",
    CIRCLE = "circle",
    NONE = "none"
}

/**
 * Element 图形元素。
 * 定义：在 G2 中，我们会将数据通过图形语法映射成不同的图形，比如点图，数据集中的每条数据会对应一个点，柱状图每条数据对应一个柱子，线图则是一组数据对应一条折线，Element 即一条/一组数据对应的图形元素，它代表一条数据或者一个数据集，在图形层面，它可以是单个 Shape 也可以是多个 Shape，我们称之为图形元素。
 */
declare class Element_2 extends Base {
    /** 用于创建各种 shape 的工厂对象 */
    shapeFactory: ShapeFactory;
    /** shape 容器 */
    container: IGroup;
    /** 最后创建的图形对象 */
    shape: IShape | IGroup;
    /** shape 的动画配置 */
    animate: AnimateOption | boolean;
    /** element 对应的 Geometry 实例 */
    geometry: Geometry;
    /** 保存 shape 对应的 label */
    labelShape: IGroup[];
    /** 绘制的 shape 类型 */
    private shapeType;
    /** shape 绘制需要的数据 */
    private model;
    /** 原始数据 */
    private data;
    private states;
    private statesStyle;
    private offscreenGroup;
    constructor(cfg: ElementCfg);
    /**
     * 绘制图形。
     * @param model 绘制数据。
     * @param isUpdate 可选，是否是更新发生后的绘制。
     */
    draw(model: ShapeInfo, isUpdate?: boolean): void;
    /**
     * 更新图形。
     * @param model 更新的绘制数据。
     */
    update(model: ShapeInfo): void;
    /**
     * 销毁 element 实例。
     */
    destroy(): void;
    /**
     * 显示或者隐藏 element。
     * @param visible 是否可见。
     */
    changeVisible(visible: boolean): void;
    /**
     * 设置 Element 的状态。
     *
     * 目前 Element 开放三种状态：
     * 1. active
     * 2. selected
     * 3. inactive
     *
     * 这三种状态相互独立，可以进行叠加。
     *
     * 这三种状态的样式可在 [[Theme]] 主题中或者通过 `geometry.state()` 接口进行配置。
     *
     * ```ts
     * // 激活 active 状态
     * setState('active', true);
     * ```
     *
     * @param stateName 状态名
     * @param stateStatus 是否开启状态
     */
    setState(stateName: string, stateStatus: boolean): void;
    /**
     * 清空状量态，恢复至初始状态。
     */
    clearStates(): void;
    /**
     * 查询当前 Element 上是否已设置 `stateName` 对应的状态。
     * @param stateName 状态名称。
     * @returns true 表示存在，false 表示不存在。
     */
    hasState(stateName: string): boolean;
    /**
     * 获取当前 Element 上所有的状态。
     * @returns 当前 Element 上所有的状态数组。
     */
    getStates(): string[];
    /**
     * 获取 Element 对应的原始数据。
     * @returns 原始数据。
     */
    getData(): Datum;
    /**
     * 获取 Element 对应的图形绘制数据。
     * @returns 图形绘制数据。
     */
    getModel(): ShapeInfo;
    /**
     * 返回 Element 元素整体的 bbox，包含文本及文本连线（有的话）。
     * @returns 整体包围盒。
     */
    getBBox(): BBox;
    private getStatesStyle;
    private getStateStyle;
    private getAnimateCfg;
    private drawShape;
    private getOffscreenGroup;
    private setShapeInfo;
    private syncShapeStyle;
    private getShapeType;
}

/** Element 构造函数传入参数类型 */
declare interface ElementCfg {
    /** 用于创建各种 shape 的工厂对象 */
    shapeFactory: ShapeFactory;
    /** shape 容器 */
    container: IGroup;
    /** 虚拟 group，用户可以不传入 */
    offscreenGroup?: IGroup;
    /** 是否可见 */
    visible?: boolean;
}

declare type EventCallback = (event: LooseObject) => void;

/**
 * todo: 事件名可穷举，后续需要补充
 * 事件配置项
 */
declare interface EventCfg {
    [key: string]: EventCallback;
}

/**
 * facet 基类
 *  - 定义生命周期，方便自定义 facet
 *  - 提供基础的生命流程方法
 *
 * 生命周期：
 *
 * 初始化 init
 * 1. 初始化容器
 * 2. 数据分面，生成分面布局信息
 *
 * 渲染阶段 render
 * 1. view 创建
 * 2. title
 * 3. axis
 *
 * 清除阶段 clear
 * 1. 清除 view
 *
 * 销毁阶段 destroy
 * 1. clear
 * 2. 清除事件
 * 3. 清除 group
 */
declare abstract class Facet<C extends FacetCfg<FacetData> = FacetCfg<FacetData>, F extends FacetData = FacetData> {
    /** 分面所在的 view */
    view: View;
    /** 分面容器 */
    container: IGroup;
    /** 是否销毁 */
    destroyed: boolean;
    /** 分面的配置项 */
    protected cfg: C;
    /** 分面之后的所有分面数据结构 */
    protected facets: F[];
    constructor(view: View, cfg: C);
    /**
     * 初始化过程
     */
    init(): void;
    /**
     * 渲染分面，由上层 view 调用。包括：
     *  - 分面 view
     *  - 轴
     *  - title
     *
     *  子类可以复写，添加一些其他组件，比如滚动条等
     */
    render(): void;
    /**
     * 更新 facet
     */
    update(): void;
    /**
     * 清空，clear 之后如果还需要使用，需要重新调用 init 初始化过程
     * 一般在数据有变更的时候调用，重新进行数据的分面逻辑
     */
    clear(): void;
    /**
     * 销毁
     */
    destroy(): void;
    /**
     * 根据 facet 生成 view，可以给上层自定义使用
     * @param facet
     */
    protected facetToView(facet: F): View;
    private createContainer;
    /**
     * 初始化 view
     */
    private renderViews;
    /**
     * 创建 分面 view
     */
    private createFacetViews;
    /**
     * 从 view 中清除 facetView
     */
    private clearFacetViews;
    /**
     * 获取这个字段对应的所有值，数组
     * @protected
     * @param data 数据
     * @param field 字段名
     * @return 字段对应的值
     */
    protected getFieldValues(data: Datum[], field: string): string[];
    /**
     * 获得每个分面的 region，平分区域
     * @param rows row 总数
     * @param cols col 总数
     * @param xIndex x 方向 index
     * @param yIndex y 方向 index
     */
    protected getRegion(rows: number, cols: number, xIndex: number, yIndex: number): Region;
    protected getDefaultCfg(): {
        eachView: any;
        showTitle: boolean;
        padding: number;
        fields: any[];
    };
    /**
     * 默认的 title 样式，因为有的分面是 title，有的分面配置是 columnTitle、rowTitle
     */
    protected getDefaultTitleCfg(): {
        style: {
            fontSize: number;
            fill: string;
            fontFamily: any;
        };
    };
    /**
     * 处理 axis 的默认配置
     * @param view
     * @param facet
     */
    protected processAxis(view: View, facet: F): void;
    /**
     * 获取分面数据
     * @param conditions
     */
    protected getFacetDataFilter(conditions: Condition[]): FacetDataFilter;
    /**
     * @override 开始处理 eachView
     * @param view
     * @param facet
     */
    protected abstract beforeEachView(view: View, facet: F): any;
    /**
     * @override 处理 eachView 之后
     * @param view
     * @param facet
     */
    protected abstract afterEachView(view: View, facet: F): any;
    /**
     * @override 生成分面数据，包含布局
     * @param data
     */
    protected abstract generateFacets(data: Datum[]): F[];
    /**
     * 获取 x 轴的配置
     * @param x
     * @param axes
     * @param option
     * @param facet
     */
    protected abstract getXAxisOption(x: string, axes: any, option: AxisCfg, facet: F): object;
    /**
     * 获取 y 轴的配置
     * @param y
     * @param axes
     * @param option
     * @param facet
     */
    protected abstract getYAxisOption(y: string, axes: any, option: AxisCfg, facet: F): object;
}

/**
 * 默认的基础配置
 */
declare interface FacetCfg<D> {
    /** 布局类型。 */
    readonly type?: string;
    /** view 创建回调。 */
    readonly eachView: (innerView: View, facet?: D) => any;
    /** facet view padding。 */
    readonly padding?: ViewPadding;
    /** 是否显示标题。 */
    readonly showTitle?: boolean;
    /** facet 数据划分维度。 */
    readonly fields: string[];
}

/**
 * facet object map
 */
declare interface FacetCfgMap {
    /** rect 类型分面配置 */
    readonly rect: RectCfg;
    /** mirror 类型分面配置 */
    readonly mirror: MirrorCfg;
    /** list 类型分面配置 */
    readonly list: ListCfg;
    /** matrix 类型分面配置 */
    readonly matrix: MatrixCfg;
    /** circle 类型分面配置 */
    readonly circle: CircleCfg;
    /** tree 类型分面配置 */
    readonly tree: TreeCfg;
}

/**
 * 分面数据
 */
declare interface FacetData {
    /** 分面类型。 */
    readonly type: string;
    /** 当前分面子 view 的数据。 */
    readonly data: object[];
    /** 当前分面子 view 的范围。 */
    readonly region: Region;
    /** 当前分面子 view 的 padding。 */
    readonly padding?: number;
    /** 当前 facet 对应生成的 view。 */
    view?: View;
    /** 分面行字段。 */
    readonly rowField: string;
    /** 分面列字段。 */
    readonly columnField: string;
    /** 当前行分面的枚举值。 */
    readonly rowValue: string;
    /** 当前列分面的枚举值。 */
    readonly columnValue: string;
    /** 当前行索引。 */
    readonly rowIndex: number;
    /** 当前列索引。 */
    readonly columnIndex: number;
    /** 当前行字段的枚举值长度。 */
    readonly rowValuesLength: number;
    /** 当前列字段的枚举值长度。 */
    readonly columnValuesLength: number;
}

declare type FacetDataFilter = (data: Datum[]) => boolean;

/**
 * Facet title 配置项
 */
declare interface FacetTitle {
    /** x 方向偏移。 */
    readonly offsetX?: number;
    /** y 方向偏移。 */
    readonly offsetY?: number;
    /** 文本样式。 */
    readonly style?: object;
    readonly formatter?: (val: any) => any;
}

declare type FilterCondition = (value: any, datum: Datum, idx?: number) => boolean;

declare interface G2LegendTitleCfg extends LegendTitleCfg {
    /** title 文本显示内容 */
    text?: string;
}

/**
 * Geometry 几何标记基类，主要负责数据到图形属性的映射以及绘制逻辑。
 */
declare class Geometry extends Base {
    /** Geometry 几何标记类型。 */
    readonly type: string;
    /** ShapeFactory 对应的类型。 */
    readonly shapeType: string;
    /** Coordinate 坐标系实例。 */
    coordinate: Coordinate;
    /** 用户绘制数据。 */
    data: Data;
    /** 图形绘制容器。 */
    readonly container: IGroup;
    /** label 绘制容器。 */
    readonly labelsContainer: IGroup;
    /** 是否对数据进行排序，默认为 false。  */
    sortable: boolean;
    /** 当前 Geometry 实例主题。  */
    theme: LooseObject;
    /** 存储 geometry 需要的 scales，需要外部传入。 */
    scales: Record<string, Scale>;
    /** scale 定义，需要外部传入。 */
    scaleDefs: Record<string, ScaleOption>;
    /** 画布区域，用于 label 布局。 */
    canvasRegion: BBox;
    /** Attribute map  */
    attributes: Record<string, Attribute>;
    /** Element map */
    elements: Element_2[];
    /**
     * 存储处理后的数据，
     * + init() 及 updateData() 逻辑后, 结构为 Data[]；
     * + paint() 逻辑后，结构为 MappingDatum[][]。
     */
    dataArray: MappingDatum[][];
    /** 存储 tooltip 配置信息。 */
    tooltipOption: GeometryTooltipOption | boolean;
    /** 存储 label 配置信息。 */
    labelOption: LabelOption | false;
    /** 状态量相关的配置项 */
    stateOption: StateOption;
    /** 使用 key-value 结构存储 Element，key 为每个 Element 实例对应的唯一 ID */
    elementsMap: Record<string, Element_2>;
    /** animate 配置项 */
    animateOption: AnimateOption | boolean;
    /** 图形属性映射配置 */
    protected attributeOption: Record<string, AttributeOption>;
    /** adjust 配置项 */
    protected adjustOption: AdjustOption[];
    /** style 配置项 */
    protected styleOption: StyleOption;
    /** custom 自定义的配置项 */
    protected customOption: CustomOption;
    /** 每个 Geometry 对应的 Shape 工厂实例，用于创建各个 Shape */
    protected shapeFactory: ShapeFactory;
    /** 存储上一次渲染时的 element 映射表，用于更新逻辑 */
    protected lastElementsMap: Record<string, Element_2>;
    /** 是否生成多个点来绘制图形。 */
    protected generatePoints: boolean;
    /** 存储发生图形属性映射前的数据 */
    protected beforeMappingData: Data[];
    /** 存储每个 shape 的默认 size，用于 Interval、Schema 几何标记 */
    protected defaultSize: number;
    private userTheme;
    private adjusts;
    private lastAttributeOption;
    private idFields;
    private geometryLabel;
    /** 组间距 */
    protected intervalPadding: number;
    /** 组内间距 */
    protected dodgePadding: number;
    /** 柱状图最大宽度 */
    protected maxColumnWidth: number;
    /** 柱状图最小宽度 */
    protected minColumnWidth: number;
    /** 一般柱状图宽度占比 */
    protected columnWidthRatio: number;
    /** 玫瑰图占比 */
    protected roseWidthRatio: number;
    /** 多层饼图/环图占比 */
    protected multiplePieWidthRatio: number;
    /** 虚拟 Group，用于图形更新 */
    private offscreenGroup;
    private groupScales;
    private hasSorted;
    protected isCoordinateChanged: boolean;
    /**
     * 创建 Geometry 实例。
     * @param cfg
     */
    constructor(cfg: GeometryCfg);
    /**
     * 配置 position 通道映射规则。
     *
     * @example
     * ```typescript
     * // 数据结构: [{ x: 'A', y: 10, color: 'red' }]
     * geometry.position('x*y');
     * geometry.position([ 'x', 'y' ]);
     * geometry.position({
     *   fields: [ 'x', 'y' ],
     * });
     * ```
     *
     * @param cfg 映射规则
     * @returns
     */
    position(cfg: string | string[] | AttributeOption): Geometry;
    /**
     * 配置 color 通道映射规则。
     *
     * @example
     * ```typescript
     * // data: [{ x: 'A', y: 10, color: 'red' }, { x: 'B', y: 30, color: 'yellow' }]
     * geometry.color({
     *   fields: [ 'x' ],
     *   values: [ '#1890ff', '#5AD8A6' ],
     * });
     * ```
     *
     * @param field 映射规则
     * @returns
     */
    color(field: AttributeOption): Geometry;
    /**
     * @example
     * ```typescript
     * // data: [{ x: 'A', y: 10, color: 'red' }, { x: 'B', y: 30, color: 'yellow' }]
     *
     * // 使用 '#1890ff' 颜色渲染图形
     * geometry.color('#1890ff');
     *
     * // 根据 x 字段的数据值进行颜色的映射，这时候 G2 会在内部调用默认的回调函数，读取默认提供的颜色进行数据值到颜色值的映射。
     * geometry.color('x');
     *
     * // 将 'x' 字段的数据值映射至指定的颜色值 colors（可以是字符串也可以是数组），此时用于通常映射分类数据
     * geometry.color('x', [ '#1890ff', '#5AD8A6' ]);
     *
     * // 使用回调函数进行颜色值的自定义；可以使用多个字段使用、*号连接
     * geometry.color('x', (xVal) => {
     *   if (xVal === 'a') {
     *     return 'red';
     *   }
     *   return 'blue';
     * });
     *
     * // 指定颜色的渐变路径，用于映射连续的数据
     * geometry.color('x', '#BAE7FF-#1890FF-#0050B3');
     * ```
     *
     * @param field 参与颜色映射的数据字段，多个字段使用 '*' 连接符进行连接。
     * @param cfg Optional, color 映射规则。
     * @returns
     */
    color(field: string, cfg?: string | string[] | ColorAttrCallback): Geometry;
    /**
     * 配置 shape 通道映射规则。
     *
     * @example
     *
     * ```typescript
     * // data: [{ x: 'A', y: 10, color: 'red' }, { x: 'B', y: 30, color: 'yellow' }]
     * geometry.shape({
     *   fields: [ 'x' ],
     * });
     * ```
     *
     * @param field 映射规则配置。
     * @returns
     */
    shape(field: AttributeOption): Geometry;
    /**
     *
     * @example
     * ```typescript
     * // data: [{ x: 'A', y: 10, color: 'red' }, { x: 'B', y: 30, color: 'yellow' }]
     *
     * // 指定常量，将所有数据值映射到固定的 shape
     * geometry.shape('circle');
     *
     * // 将指定的字段映射到内置的 shapes 数组中
     * geometry.shape('x');
     *
     * // 将指定的字段映射到指定的 shapes 数组中
     * geometry.shape('x', [ 'circle', 'diamond', 'square' ]);
     *
     * // 使用回调函数获取 shape，用于个性化的 shape 定制，可以根据单个或者多个字段确定
     * geometry.shape('x', (xVal) => {
     *   if (xVal === 'a') {
     *     return 'circle';
     *   }
     *   return 'diamond';
     * });
     * ```
     *
     * @param field 参与 shape 映射的数据字段，多个字段使用 '*' 连接符进行连接。
     * @param cfg Optional, shape 映射规则。
     * @returns
     */
    shape(field: string, cfg?: string[] | ShapeAttrCallback): Geometry;
    /**
     * 配置 size 通道映射规则。
     *
     * @example
     * ```typescript
     * // data: [{ x: 'A', y: 10, color: 'red' }, { x: 'B', y: 30, color: 'yellow' }]
     * geometry.size({
     *   values: [ 10 ],
     * })
     * ```
     *
     * @param field 映射规则。
     * @returns
     */
    size(field: AttributeOption): Geometry;
    /**
     *
     * @example
     * ```typescript
     * // data: [{ x: 'A', y: 10, color: 'red' }, { x: 'B', y: 30, color: 'yellow' }]
     *
     * // 直接指定像素大小
     * geometry.size(10);
     *
     * // 指定映射到 size 的字段，使用内置的默认大小范围为 [1, 10]
     * geometry.size('x');
     *
     * // 指定映射到 size 字段外，还提供了 size 的最大值和最小值范围
     * geometry.size('x', [ 5, 30 ]);
     *
     * // 使用回调函数映射 size，用于个性化的 size 定制，可以使用多个字段进行映射
     * geometry.size('x', (xVal) => {
     *   if (xVal === 'a') {
     *     return 10;
     *   }
     *   return 5;
     * });
     * ```
     *
     * @param field 参与 size 映射的数据字段，多个字段使用 '*' 连接符进行连接。
     * @param cfg Optional, size 映射规则
     * @returns
     */
    size(field: number | string, cfg?: [number, number] | SizeAttrCallback): Geometry;
    /**
     * 设置数据调整方式。G2 目前内置了四种类型：
     * 1. dodge
     * 2. stack
     * 3. symmetric
     * 4. jitter
     *
     *
     * **Tip**
     * + 对于 'dodge' 类型，可以额外进行如下属性的配置:
     * ```typescript
     * geometry.adjust('dodge', {
     *   marginRatio: 0, // 取 0 到 1 范围的值（相对于每个柱子宽度），用于控制一个分组中柱子之间的间距
     *   dodgeBy: 'x', // 该属性只对 'dodge' 类型生效，声明以哪个数据字段为分组依据
     * });
     * ```
     *
     * + 对于 'stack' 类型，可以额外进行如下属性的配置:
     * ```typescript
     * geometry.adjust('stack', {
     *   reverseOrder: false, // 用于控制是否对数据进行反序操作
     * });
     * ```
     *
     * @example
     * ```typescript
     * geometry.adjust('stack');
     *
     * geometry.adjust({
     *   type: 'stack',
     *   reverseOrder: false,
     * });
     *
     * // 组合使用 adjust
     * geometry.adjust([ 'stack', 'dodge' ]);
     *
     * geometry.adjust([
     *   { type: 'stack' },
     *   { type: 'dodge', dodgeBy: 'x' },
     * ]);
     * ```
     *
     * @param adjustCfg 数据调整配置
     * @returns
     */
    adjust(adjustCfg: string | string[] | AdjustOption | AdjustOption[]): Geometry;
    /**
     * 图形样式配置。
     *
     * @example
     * ```typescript
     * // 配置图形样式
     * style({
     *   lineWidth: 2,
     *   stroke: '#1890ff',
     * });
     *
     * // 根据具体的数据进行详细配置
     * style({
     *   fields: [ 'x', 'y' ], // 数据字段
     *   callback: (xVal, yVal) => {
     *     const style = { lineWidth: 2, stroke: '#1890ff' };
     *     if (xVal === 'a') {
     *       style.lineDash = [ 2, 2 ];
     *     }
     *     return style;
     *   },
     * });
     * ```
     *
     * @param field 配置样式属性或者样式规则。
     * @returns
     */
    style(field: StyleOption | LooseObject): Geometry;
