     * @returns draw cfg
     */
    protected getDrawCfg(mappingDatum: MappingDatum): ShapeInfo;
    /**
     * 创建所有的 Elements。
     * @param mappingData
     * @param [isUpdate]
     * @returns elements
     */
    protected createElements(mappingData: MappingDatum[], index: number, isUpdate?: boolean): Element_2[];
    /**
     * 获取渲染的 label 类型。
     */
    protected getLabelType(): string;
    /**
     * 获取 Y 轴上的最小值。
     */
    protected getYMinValue(): number;
    private createAttrOption;
    private initAttributes;
    private processData;
    private adjustData;
    private groupData;
    private updateStackRange;
    private beforeMapping;
    private generateShapePoints;
    private normalizeValues;
    private mapping;
    private convertPoint;
    private getStyleCfg;
    private setCfg;
    private renderLabels;
    /**
     * 是否需要进行群组入场动画
     * 规则：
     * 1. 如果发生更新，则不进行
     * 2. 如果用户关闭 geometry 动画，则不进行
     * 3. 如果用户关闭了 appear 动画，则不进行
     * 4. 如果用户配置了 appear.animation，则不进行
     */
    private canDoGroupAnimation;
}

/** Geometry 构造函数参数 */
declare interface GeometryCfg {
    /** Geometry shape 的容器。 */
    container: IGroup;
    /** 绘制的坐标系对象。 */
    coordinate?: Coordinate;
    /** 绘制数据。 */
    data?: Data;
    /** 需要的 scales。 */
    scales?: Record<string, Scale>;
    /** 列定义 */
    scaleDefs?: Record<string, ScaleOption>;
    /** Geometry labels 的容器 */
    labelsContainer?: IGroup;
    /** 是否对数据进行排序 */
    sortable?: boolean;
    /** 是否可见 */
    visible?: boolean;
    /** 主题配置 */
    theme?: LooseObject;
    /** 组间距 */
    intervalPadding?: number;
    /** 组内间距 */
    dodgePadding?: number;
    /** 柱状图最大宽度 */
    maxColumnWidth?: number;
    /** 柱状图最小宽度 */
    minColumnWidth?: number;
    /** 默认宽度占比，interval类型和schema类型通用 */
    columnWidthRatio?: number;
    /** 玫瑰图占比 */
    roseWidthRatio?: number;
    /** 多层饼图/环图占比 */
    multiplePieWidthRatio?: number;
}

/** geometry.label({}) 配置属性 */
declare interface GeometryLabelCfg {
    /**
     * 用于声明渲染的 label 类型。
     * 当用户使用了自定义的 label 类型，需要声明具体的 type 类型，否则会使用默认的 label 类型渲染。
     */
    type?: string;
    /** 相对数据点的偏移距离, polar 和 theta 坐标系下可使用百分比字符串。 */
    offset?: number | string;
    /** label 相对于数据点在 X 方向的偏移距离。 */
    offsetX?: number;
    /** label 相对于数据点在 Y 方向的偏移距离。 */
    offsetY?: number;
    /**
     * 展示的文本内容，如果不声明则按照参与映射的第一字段的值进行显示。
     * 当 content 为 IGroup 或者 IShape 类型时，请使用相对定位，即 x 和 y 坐标都设为 0，G2 内部会整体做最后的 label 进行定位的。
     * 示例： https://g2.antv.vision/zh/examples/pie/basic#pie-custome-label
     */
    content?: string | IGroup | IShape | GeometryLabelContentCallback;
    /** label 文本图形属性样式。 */
    style?: LooseObject;
    /** label 是否自动旋转，默认为 true。 */
    autoRotate?: boolean;
    /**
     * 当且仅当 `autoRotate` 为 false 时生效，用于设置文本的旋转角度，**弧度制**。
     */
    rotate?: number;
    /** 标签高度设置，仅当标签类型 type 为 pie 时生效；也可在主题中设置 pieLabels.labelHeight */
    labelHeight?: number;
    /**
     * 用于设置文本连接线的样式属性，null 表示不展示。
     */
    labelLine?: null | boolean | {
        style?: object;
    };
    /** 只对极坐标下的文本生效，表示文本是否按照角度进行放射状显示，true 表示开启，false 表示关闭。 */
    labelEmit?: boolean;
    /**
     * 文本布局类型，支持多种布局函数组合使用。
     *
     * 目前提供了三种：'overlap'，'fixedOverlap'，'limitInShape'：
     * 1. overlap: label 防遮挡，为了防止 label 之间相互覆盖，通过尝试向**四周偏移**来剔除放不下的 label。
     * 2. fixed-overlap: 不改变 label 位置的情况下对相互重叠的 label 进行调整。
     * 3. limit-in-shape: 剔除 shape 容纳不了的 label。
     *
     * @example
     * ```ts
     * layout: {
     *   type: 'overlap',
     * },
     * ```
     */
    layout?: GeometryLabelLayoutCfg | GeometryLabelLayoutCfg[];
    /**
     * 用于绘制 label 背景
     */
    background?: {
        /**
         * 背景框 图形属性配置
         * - fill?: string; 背景框 填充色
         * - stroke?: string; 背景框 描边色
         * - lineWidth?: string; 背景框 描边宽度
         * - radius?: number | number[]; 背景框圆角，支持整数或数组形式
         */
        style?: ShapeAttrs;
        /** 背景框 内边距 */
        padding?: number | number[];
    };
    /**
     * 仅当 geometry 为 interval 时生效，指定当前 label 与当前图形的相对位置。
     */
    position?: ((data: Datum, mappingData: MappingDatum, index: number) => IntervalGeometryLabelPosition) | IntervalGeometryLabelPosition;
    /** 动画配置。 */
    animate?: AnimateOption | false | null;
}

/** geometry label 中 content 属性的回调函数类型定义 */
declare type GeometryLabelContentCallback = (data: Datum, mappingData: MappingDatum, index: number) => string | IShape | IGroup;

declare interface GeometryLabelLayoutCfg {
