    canvas: ICanvas;
    /** 存储最终计算的 padding 结果 */
    autoPadding: PaddingCal;
    /** 三层 Group 图形中的背景层。 */
    backgroundGroup: IGroup;
    /** 三层 Group 图形中的中间层。 */
    middleGroup: IGroup;
    /** 三层 Group 图形中的前景层。 */
