},
})

 = registerShapeFactory('point', {
defaultShapeType: 'hollow-circle',
getDefaultPoints(pointInfo: ShapePoint): Point[] {
return splitPoints(pointInfo);
},
})

 = registerShapeFactory('polygon', {
defaultShapeType: 'polygon',
getDefaultPoints(pointInfo: ShapePoint) {
const points = [];
each(pointInfo.x as number[], (subX, index) => {
const subY = pointInfo.y[index];
points.push({
x: subX,
y: subY,
});
});
return points;
},
})

